$(document).ready(function(){	$(".heroskills").hide();	$(".herotalents").hide();	    $(".skillsname").click(function(){        $(this).siblings(".heroskills").slideToggle(200);    });	$(".talentsname").click(function(){        $(this).siblings(".herotalents").slideToggle(200);    });		$("#options").click(function(){      if($("#menu").hasClass('hide')) {        $("#menu").animate({height:'10px'},200).removeClass('hide');		$("#scrollcontainer").animate({top:'70px'},200).removeClass('hide');      } else {         $("#menu").animate({height:'50px'},200).addClass('hide');		$("#scrollcontainer").animate({top:'120px'},200).removeClass('hide');      }    });});