$(document).ready(function(){	$("div#heroskills").hide();	$("div#herotalents").hide();	    $("div#skillsname").click(function(){        $(this).siblings("div#heroskills").slideToggle(200);    });	$("div#talentsname").click(function(){        $(this).siblings("div#herotalents").slideToggle(200);    });		$("div#options").click(function(){      if($("div.menu").hasClass('hide')) {        $("div.menu").animate({height:'1.5%'},200).removeClass('hide');		$(".scrollcontainer").animate({height:'83.5%'},200).removeClass('hide');      } else {         $("div.menu").animate({height:'8%'},200).addClass('hide');		$(".scrollcontainer").animate({height:'77%'},200).removeClass('hide');      }    });});